import React, {Component} from 'react';
import axios from 'axios';
import {Card, Button,Form } from 'react-bootstrap';

export default class Itemslist extends Component {

    constructor(props) {
        super(props);
        this.state = {users: [],
            ori:[],
            qty:0,
            email:'',
            id:0
        }
        this.onChangeQty = this.onChangeQty.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this);
        this.fun = this.fun.bind(this);
        this.onChangeId = this.onChangeId.bind(this);
    }
    onChangeQty(event) {
        this.setState({ qty: event.target.value });
    }
    onChangeId(event) {
        this.setState({ id: event.target.value });
    }

    onChangeEmail(event) {
        this.tmp=event.target.value;
        this.x=[];
        // console.log(this.state.email)
        for (const item of this.state.ori){
            if((typeof item.name)=="string"&&item.name.indexOf(this.tmp)!=-1)
                this.x.push(item);
        }
        this.setState({users: this.x});
        this.setState({ email: event.target.value });
        // console.log(this.x);
    }

    componentDidMount() {
        const Token = {
            token: sessionStorage.getItem("zzz")
        }
        axios.post('http://localhost:4000/item/',Token)
            .then(response => {
                console.log("kk"+ response.data);
                this.setState({users: response.data,ori:response.data});
            })
            .catch(function(error) {
                console.log(error);
            })
    }
    fun(){
        console.log(this.state)
    }

    render() {
        return(
            <div>
            <h1>ORDER ITEMS</h1>
            <div className="form-group">
            <label>search: </label>
            <input type="text" 
            className="form-control" 
            value={this.state.email}
            onChange={this.onChangeEmail}
            />  
            </div>
            <table className="table table-striped">
            <thead>
            <tr>
            <th>id</th>
            <th>Name</th>
            <th>Price</th>
            <th>Quantity Available</th>
            <th>Order Quantity</th>
            <th>Order</th>
            </tr>
            </thead>
            <tbody>
            { 
                this.state.users.map((currentUser, i) => {
                    return (
                        <tr>
                        <td>{i}</td>
                        <td>{currentUser.name}</td>
                        <td>{currentUser.price}</td>
                        <td>{currentUser.quantity}</td>
                        </tr>
                    )
                })
            }
            </tbody>
            </table>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                        <label>Id: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.id}
                               onChange={this.onChangeId}
                               />
                    </div>

                    <div className="form-group">
                        <label>Quantity: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.qty}
                               onChange={this.onChangeId}
                               />  
                    </div>

                    <div className="form-group">
                        <input type="submit" value="Place Order" className="btn btn-primary"/>
                    </div>
                </form>
            </div>
        )
    }
}
