import React, {Component} from 'react';
import axios from 'axios';
import {Card, Button,Form } from 'react-bootstrap';

export default class Itemslist extends Component {

    constructor(props) {
        super(props);
        this.state = {users: [],
            qty:0
        }
        this.onChangeQty = this.onChangeQty.bind(this);
    }
    onChangeQty(event) {
        this.setState({ qty: event.target.value });
    }


    componentDidMount() {
        const Token = {
            token: sessionStorage.getItem("zzz")
        }
        axios.post('http://localhost:4000/item/',Token)
            .then(response => {
                console.log("kk"+ response.data);
                this.setState({users: response.data});
            })
            .catch(function(error) {
                console.log(error);
            })
    }

    render() {
        return(
            <div>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Price</th>
                            <th>Quantity</th>
                            <th>Order</th>
                        </tr>
                    </thead>
                    <tbody>
                    { 
                        this.state.users.map((currentUser, i) => {
                            return (
                                <tr>
                                    <td>{currentUser.name}</td>
                                    <td>{currentUser.price}</td>
                                    <td> 
                                        <input type="text" 
                                               className="input" 
                                               value={this.state.qty}
                                           onChange={this.onChangeQty}
                                           />  
                                     </td>
                                    <td>
                                            <input type="submit" value="Order" className={this.state.btncolor}/>
                                    </td>
                                </tr>
                            )
                        })
                    }
                    </tbody>
                </table>
            </div>
        )
    }
}
