import React, {Component} from 'react';
import axios from 'axios';
import {Card, Button } from 'react-bootstrap';

export default class Itemslist extends Component {

    constructor(props) {
        super(props);
        this.state = {users: []}
    }

    componentDidMount() {
        const Token = {
            token: sessionStorage.getItem("zzz")
        }
        axios.post('http://localhost:4000/item/',Token)
            .then(response => {
                console.log("kk"+ response.data);
                this.setState({users: response.data});
            })
            .catch(function(error) {
                console.log(error);
            })
    }

    render() {
        return (
            <div>
            <container>
            { 
                this.state.users.map((currentUser, i) => {
                    return (
                        <span>
                        <Card style={{ width: '18rem' }}>
                        <Card.Body>
                        <Card.Title>
                        {currentUser.name}
                        </Card.Title>
                        <Card.Subtitle className="mb-2 text-muted">Available</Card.Subtitle>
                        <Card.Text>
                        Price:{currentUser.price}
                        <br/>
                        Quantity:{currentUser.quantity}
                        </Card.Text>
                        <Card.Link href="#">Card Link</Card.Link>
                        <Card.Link href="#">Another Link</Card.Link>
                        </Card.Body>
                        </Card>
                        </span>
                    )
                })
            }
            </container>
            </div>
        )
    }
}
