import React, {Component} from 'react';
import axios from 'axios';

export default class Itemslist extends Component {

    constructor(props) {
        super(props);
        this.state = {users: [],
        id:0}
        this.fun = this.fun.bind(this);
        this.onChangeId = this.onChangeId.bind(this);
    }

    componentDidMount() {
        const Token = {
            token: sessionStorage.getItem("zzz")
        }
        axios.post('http://localhost:4000/item/',Token)
             .then(response => {
                 console.log("kk"+ response.data);
                 this.setState({users: response.data});
             })
             .catch(function(error) {
                 console.log(error);
             })
    }

    render() {
        return (
            <div>
            <h1> YOUR PRODUCTS</h1>
                <table className="table table-striped">
                    <thead>
                        <tr>
                            <th>Name</th>
                            <th>Price</th>
                            <th>Quantity</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                    { 
                        this.state.users.map((currentUser, i) => {
                            return (
                                <tr>
                                    <td>{currentUser.name}</td>
                                    <td>{currentUser.price}</td>
                                    <td>{currentUser.quantity}</td>
                                    <td>
                        <input type="submit" value="Cancel" className="btn btn-primary"/>
                                </td>
                                </tr>
                            )
                        })
                    }
                    </tbody>
                </table>
                    <div className="form-group">
                        <label>Id: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.id}
                               onChange={this.onChangeId}
                               />
                    </div>

                    <div className="form-group">
                        <label>Quantity: </label>
                        <input type="text" 
                               className="form-control" 
                               value={this.state.qty}
                               onChange={this.onChangeQty}
                               />  
                    </div>

                    <div className="form-group">
                        <input type="submit" onClick={this.fun} value="Place Order" className="btn btn-primary"/>
                    </div>
            </div>
        )
    }
}
